import { ref } from 'vue';
import getCurrentUser from '../composables/getCurrentUser';
import { timestamp } from '../firebase/config';
export default {
  setup() {
    const {
      user
    } = getCurrentUser();
    const message = ref('');
    const {
      addDoc,
      error
    } = useCollection('messages');
    const handleSubmit = async () => {
      const chat = {
        name: user.value.displayName,
        message: message.value,
        createdAt: timestamp()
      };
      await addDoc(chat);
      //console.log(chat)
      if (!error.val) {}
      message.value = '';
    };
    return {
      message,
      handleSubmit,
      error
    };
  }
};
